import React from 'react';
import { UserAuth } from '../context/AuthContext';

const Header = () => {
  const { user } = UserAuth();
  
  return (
    <div className="flex justify-between items-center">
      <div className="flex items-center">
        <span className="text-black mr-2">{user?.email}</span>
        {/* Replace profile-pic.jpg with Account Icon.png located in public */}
        <img src="/Account Icon.png" alt="Profile" className="w-10 h-10 rounded-full" />
      </div>
    </div>
  );
};

export default Header;
