import React, { useState, useEffect } from 'react';
import { UserAuth } from '../context/AuthContext';

const ExpenseReport = () => {
  const { user } = UserAuth();
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (user) {
      setLoading(true);
      fetch(`/api/expenses/report/${user.uid}`)
        .then(res => res.json())
        .then(data => {
          setReportData(data);
          setLoading(false);
        })
        .catch(err => {
          console.log(err);
          setLoading(false);
        });
    }
  }, [user]);

  return (
    <div className="flex">
      {/* Side Navigation */}
      <div className="w-1/4 bg-gray-800 text-white">
        <nav className="py-4 px-6">
          <h3 className="text-xl">Expenses</h3>
          <ul>
            <li><a href="/add-expense" className="block py-2">Add Expense</a></li>
            <li><a href="/expense-report" className="block py-2">Expense Report</a></li>
            <li><a href="/my-expenses" className="block py-2">My Expenses</a></li>
            {/* Add more options if needed */}
          </ul>
        </nav>
      </div>

      {/* Main Content */}
      <div className="w-3/4 p-6">
        <h2 className="text-3xl font-bold">Expense Report Q2</h2>

        {loading ? (
          <p>Loading...</p>
        ) : reportData ? (
          <div>
            <table className="min-w-full table-auto border-collapse">
              <thead>
                <tr>
                  <th className="border px-4 py-2 text-left">Expenses</th>
                  <th className="border px-4 py-2 text-left">April</th>
                  <th className="border px-4 py-2 text-left">May</th>
                  <th className="border px-4 py-2 text-left">June</th>
                  <th className="border px-4 py-2 text-left">Total</th>
                </tr>
              </thead>
              <tbody>
                {reportData.map((expense, index) => (
                  <tr key={index}>
                    <td className="border px-4 py-2">{expense.name}</td>
                    <td className="border px-4 py-2">${expense.april}</td>
                    <td className="border px-4 py-2">${expense.may}</td>
                    <td className="border px-4 py-2">${expense.june}</td>
                    <td className="border px-4 py-2">${expense.total}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <p className="text-right mt-4">
              Total Expenses: ${reportData.reduce((sum, expense) => sum + expense.total, 0)}
            </p>
          </div>
        ) : (
          <p>No report data available.</p>
        )}
      </div>
    </div>
  );
};

export default ExpenseReport;
