import React from 'react';
import { Link } from 'react-router-dom';
import { UserAuth } from '../context/AuthContext';

const Account = () => {
  const { user, logout } = UserAuth();

  const handleLogout = async () => {
    try {
      await logout();
      console.log('You are logged out');
    } catch (e) {
      console.log(e.message);
    }
  };

  return (
    <div className='bg-background min-h-screen flex items-center justify-center'>
      <div className='bg-white p-8 rounded shadow-lg max-w-md w-full'>
        <h1 className='text-3xl font-bold text-primary mb-6'>Account</h1>
        <p className='text-lg text-textColor mb-4'>
          <span className='font-semibold'>User Email:</span> {user && user.email}
        </p>
        <button
          onClick={handleLogout}
          className='bg-secondary text-white py-2 px-6 rounded hover:bg-primary transition duration-200'>
          Logout
        </button>

        <div className='mt-8'>
          <h2 className='text-2xl font-bold text-primary mb-4'>Navigate:</h2>
          <ul className='list-disc list-inside'>
            <li className='text-lg hover:text-secondary transition duration-200'>
              <Link to='/add-expense'>Add Expense</Link>
            </li>
            <li className='text-lg hover:text-secondary transition duration-200'>
              <Link to='/expense-report'>View Expense Report</Link>
            </li>
            <li className='text-lg hover:text-secondary transition duration-200'>
              <Link to='/manage-categories'>Manage Categories</Link>
            </li>
            <li className='text-lg hover:text-secondary transition duration-200'>
              <Link to='/search-expenses'>Search Expenses</Link>
            </li>
            <li className='text-lg hover:text-secondary transition duration-200'>
              <Link to='/spending-analysis'>Spending Analysis</Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Account;
